<template>    <div id="movie-list" class="row">        <div>            <router-link class="btn btn-default" :to="{path: '/add-movie'}">Add movie</router-link>        </div>        <div>            <div class="form-group">                <label for="search-element" style="margin-top: 10px;">Search Movie</label>                <input v-model="searchKey" class="form-control" id="search-element"/>            </div>        </div>        <table class="table">            <thead>            <tr>                <th>Title</th>                <th>Actions</th>            </tr>            </thead>            <tbody>            <tr v-for="movie in filteredMovies">                <td>                    <router-link :to="{name: 'movie', params: {movie_id: movie.id}}">{{ movie.title }}</router-link>                </td>                <td>                    <router-link class="btn btn-info"                                 :to="{name: 'movie-edit', params: {movie_id: movie.id}}">Edit                    </router-link>                    <router-link class="btn btn-danger"                                 :to="{name: 'movie-delete', params: {movie_id: movie.id}}">Delete                    </router-link>                </td>            </tr>            </tbody>        </table>    </div></template><script>    export default {        data(){            return {                movies: movies, searchKey: ''            };        },        computed: {            filteredMovies: function () {                var self = this;                return self.movies.filter(function (movie) {                    return movie.title.toLowerCase().indexOf(self.searchKey.toLowerCase()) !== -1                });            }        }    }</script><style></style>